mapper.select:
select a.ubic_id ,a.ubic_nomb ,a.ubic_desc ,a.ubic_vige ,b.tokn_id from rcb_ubic a inner join rcb_tokn b on (a.ubic_id = b.ubic_id)
and b.tokn_id = #{token}
and a.ubic_id = #{id}
and b.tokn_id = 'X'

select a.cabl_id ,a.cabl_nomb ,a.cabl_nume ,a.cabl_vige ,b.ubic_id ,b.ubic_nomb ,b.ubic_desc ,b.ubic_vige ,d.tokn_id ,c.estc_id ,c.estc_nomb ,c.estc_vige from rcb_caja_bloq a inner join rcb_ubic b on (a.ubic_id = b.ubic_id) inner join rcb_estc c on (b.estc_id = c.estc_id) inner join rcb_tokn d on (b.ubic_id = d.ubic_id) where a.ubic_id = #{ubicacion.id}
and a.cabl_id = #{caja.id}
and a.cabl_nume = #{caja.numero}
and a.cabl_vige = #{vigencia}
order by b.ubic_nomb ,a.cabl_nume

select count(*)
from   rcb_libr_ctrl_bloq a inner join rcb_caja_bloq b
   on (a.cabl_id = b.cabl_id) 



            a.cabl_id = #{id}


                b.cabl_nume = #{numero}
            and b.ubic_id   = #{ubicacion.id}

mapper.update:
update rcb_caja_bloq set
       cabl_nume = #{numero}
      ,cabl_nomb = #{nombre}
      ,cabl_vige = #{vigente}
      ,ubic_id   = #{ubicacion.id}
      ,audi_fech_modi = localtimestamp
where  cabl_id = #{id}

mapper.select:
select a.cand_id ,a.cand_nume ,a.cand_seri ,a.cand_vige ,b.dval_id ,b.dval_codi ,b.dval_nomb ,b.dval_vige ,b.dval_domi ,c.pers_id ,c.pers_rut ,c.pers_nomb ,d.ubic_id ,d.ubic_nomb ,d.ubic_desc ,d.ubic_vige ,e.tokn_id from rcb_cand a inner join rcb_domi_valo b on (a.dval_id_uso = b.dval_id) left join rcb_pers c on (a.pers_id = c.pers_id) inner join rcb_ubic d on (a.ubic_id = d.ubic_id) inner join rcb_tokn e on (d.ubic_id = e.ubic_id) where a.ubic_id = #{ubicacion.id}
and a.cand_id = #{candado.id}
and a.cand_nume = #{candado.numero}
and a.cand_seri = #{serie}
c.pers_rut = #{persona.rut,typeHandler=RutTypeHandler}
and a.cand_vige = #{vigencia}
order by a.ubic_id ,a.cand_seri

select count(*)
from   rcb_dota_lcbl
where  lcbl_id = #{id}

mapper.update:
update rcb_cand set
       cand_seri   = #{serie}
      ,cand_nume   = #{numero}
      ,cand_vige   = #{vigente}
      ,dval_id_uso = #{uso.id}
      ,pers_id     = #{persona.id}
      ,ubic_id     = #{ubicacion.id}
      ,audi_fech_modi = localtimestamp
where  cand_id = #{id}

mapper.select:
select a.pers_id ,a.pers_rut ,a.pers_nomb from rcb_pers a
a.pers_id = #{persona.id}
a.pers_rut = #{persona.rut,typeHandler=RutTypeHandler}
order by a.pers_rut

mapper.update:
update rcb_pers set
       pers_nomb      = #{nombre}
      ,pers_rut       = #{rut,typeHandler=RutTypeHandler}
      ,audi_fech_modi = localtimestamp
where  pers_id = #{id}

mapper.select:
select b.pers_id ,b.pers_rut ,b.pers_nomb ,a.trab_carg ,a.trab_curs_bloq ,a.trab_vige ,c.empr_id ,c.empr_rut ,c.empr_nomb ,c.empr_vige from rcb_trab a inner join rcb_pers b on (a.pers_id = b.pers_id) inner join rcb_empr c on (a.empr_id = c.empr_id)
b.pers_id = #{trabajador.id}
b.pers_rut = #{trabajador.rut,typeHandler=RutTypeHandler}
and c.empr_id = #{empresa.id}
and c.empr_rut = #{empresa.rut,typeHandler=RutTypeHandler}
and a.trab_vige = #{vigencia}
order by c.empr_rut ,b.pers_rut

mapper.update:
update rcb_trab set
       trab_carg      = #{cargo}
      ,trab_curs_bloq = #{tieneCursoBloqueo}
      ,trab_vige      = #{vigente}
where  pers_id = #{id}
  and  empr_id = #{empresa.id}

mapper.delete:
delete from rcb_trab
where  pers_id = #{id}
  and empr_id = #{empresa.id}

mapper.select:
